mapper.update:
update ahs_ask_report
reason=#{reason,jdbcType=VARCHAR}, status = #{status,jdbcType=INTEGER},
where id=#{id}

mapper.select:
select id,user_id,ask_id,status,createtime,reason,type,(select nickname from ahs_users where id=ahs_ask_report.user_id) as nickname
from ahs_ask_report
where id = #{id,jdbcType=INTEGER}

		select id,user_id,ask_id,status,createtime,reason,type,(select nickname from ahs_users where id=ahs_ask_report.user_id) as nickname
		from ahs_ask_report

			and status = #{status,jdbcType=INTEGER}
			 and user_id=#{userId,jdbcType=INTEGER}
			 and ask_id=#{askId,jdbcType=INTEGER}
			 and type=#{type,jdbcType=INTEGER}
			 and createtime BETWEEN #{startDate} and #{endDate}



              ${orderByString}


              order by id desc

		select id,user_id,ask_id,status,createtime,reason,type,(select nickname from ahs_users where id=ahs_ask_report.user_id) as nickname
		from ahs_ask_report

			and status = #{status,jdbcType=INTEGER}
			 and user_id=#{userId,jdbcType=INTEGER}
			 and ask_id=#{askId,jdbcType=INTEGER}
			 and type=#{type,jdbcType=INTEGER}
			 and createtime BETWEEN #{startDate} and #{endDate}



              ${orderByString}


              order by id desc
