mapper.update:
update ahs_user_history
user_id = #{userId,jdbcType=INTEGER}, history_type = #{historyType,jdbcType=INTEGER}, house_id = #{houseId,jdbcType=INTEGER}, house_price = #{housePrice,jdbcType=DECIMAL}, house_picture = #{housePicture,jdbcType=VARCHAR}, createtime = #{createtime,jdbcType=TIMESTAMP}, is_del = #{isDel,jdbcType=INTEGER},
where id=#{id}

mapper.select:
		select
		id,
		user_id,
		history_type,
		house_id,
		createtime,
		is_del,house_picture,house_price,house_unit,house_area,house_street,house_title,house_content,
		(CASE history_type  WHEN 1 THEN (select status from ahs_new_house where id=ahs_user_history.house_id )
		WHEN 2 THEN (select flag from ahs_second_house where id=ahs_user_history.house_id)
		WHEN 3 THEN  (select flag from ahs_rent_house where id=ahs_user_history.house_id)
		WHEN 4 THEN (select flag from ahs_shop where id=ahs_user_history.house_id)
		WHEN 5 THEN (SELECT flag from ahs_office where id=ahs_user_history.house_id) END) as house_flag
		from ahs_user_history

		 and ahs_user_history.id = #{id,jdbcType=INTEGER}
		 and ahs_user_history.user_id = #{userId,jdbcType=INTEGER}
		 and ahs_user_history.history_type = #{historyType,jdbcType=INTEGER}
		 and ahs_user_history.house_id = #{houseId,jdbcType=INTEGER}
		 and ahs_user_history.createtime = #{createtime,jdbcType=TIMESTAMP}
		 and ahs_user_history.is_del = #{isDel,jdbcType=INTEGER}



              ${orderByString}


              order by id desc

