mapper.insert:
		insert into ahs_user_spread_log (
		user_id,
		parent_id
		)
 	   values (
		 #{userId,jdbcType=INTEGER},
		 #{parentId,jdbcType=INTEGER}
		)

mapper.select:
		select
		id,
		user_id,
		parent_id,
		createtime
		from ahs_user_spread_log

		 and id = #{id,jdbcType=INTEGER}
		 and user_id = #{userId,jdbcType=INTEGER}
		 and parent_id = #{parentId,jdbcType=INTEGER}



              ${orderByString}


              order by id desc

select
id,
user_id,
parent_id
createtime
from ahs_user_spread_log

	 and id = #{id,jdbcType=INTEGER}
	 and user_id = #{userId,jdbcType=INTEGER}
	 and parent_id = #{parentId,jdbcType=INTEGER}



		${orderByString}


		order by id desc

select
(select
count(*)
from ahs_user_spread_log

	 and parent_id = #{parentId,jdbcType=INTEGER}
	 and createtime BETWEEN #{startDate,jdbcType=VARCHAR} and #{endDate,jdbcType=VARCHAR}

) as total,
(select count(*) from ahs_user_spread_log

	to_days(createtime) = to_days(now())
	 and parent_id = #{parentId,jdbcType=INTEGER}

  ) as today
from dual
